mapper.select:
select * from
(select m.*,rownum rn from 
(select * from good order by 1 desc) m where rownum <= ${currPage} * ${pageSize}) where rn > (${currPage}-1) * ${pageSize}

select * from
(select m.*,rownum rn from
( order by 1 desc) m  where ${currPage} * ${pageSize} >= rownum) where rn > (${currPage} - 1) * ${pageSize}

select * from
(select m.*,rownum rn from
( order by 1 desc) m  where ${currPage} * ${pageSize} >= rownum) where rn > (${currPage} - 1) * ${pageSize}
